import { Component } from '@angular/core';
import { ApiService } from './api.service';
import { Observable } from 'rxjs';
import { Produit } from './models/produit';

@Component({
  selector: 'my-app',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  name = 'Angular';
  login: string = '';
  password: string = '';

  nom: string = '';
  prenom: string = '';
  cnx: boolean = false;
  produits$: Observable<Array<Produit>>;
  constructor(private apiService: ApiService) {
    this.produits$ = this.apiService.getCalague();
  }
  connexion() {
    this.apiService.loginClient(this.login, this.password).subscribe((c) => {
      this.nom = c.nom;
      this.prenom = c.prenom;
      this.cnx = true;
    });
  }
}
<h1>Archi Web - Connexion backend</h1>
<label for="login">login : </label><input [(ngModel)]="login" /><br />
<label for="password">password : </label
><input [(ngModel)]="password" type="password" /><br />
<br />
<button (click)="connexion()">Connexion</button>

<hr />
<div *ngIf="cnx">
  Bonjour {{ nom }} - {{ prenom }}

  <h2>Catalogue produits</h2>

  <div *ngIf="produits$ | async as produits">
    <div *ngFor="let p of produits">
      <div>{{ p.ref }} - {{ p.titre }} - {{ p.prix }} €</div>
    </div>
  </div>
</div>
